mapper.update:
UPDATE
	MENUINFOLIST
SET
	CONTENTPAGE_YN = #{contentpageYn}
	,
	CONTENT_ID = 0

WHERE
	PAGE_NM = #{pageNm}
	AND DEPTH1 = #{depth1}
	AND DEPTH2 = #{depth2}
	AND DEPTH3 = #{depth3}

UPDATE
	MENUINFOLIST
SET
	CONTENT_ID = #{nttId}
WHERE
	PAGE_NM = #{pageNm}
	AND DEPTH1 = #{depth1}
	AND DEPTH2 = #{depth2}
	AND DEPTH3 = #{depth3}

mapper.select:
SELECT PAGE_NM AS pageNm, DEPTH1 AS depth1, DEPTH2 AS depth2, DEPTH3 AS depth3, MENU_NM AS menuNm, CONTENTPAGE_YN AS contentpageYn, HEADER_YN AS headerYn, LINK AS link, TARGET AS target, CONTENT_ID AS contentId FROM MENUINFOLIST WHERE 1=1 AND USE_YN = 'Y'
AND PAGE_NM = #{value}
ORDER BY DEPTH1 ASC, DEPTH2 ASC, DEPTH3 ASC
