mapper.insert:

INSERT INTO BBSADDEDCOLMNS
(NTT_ID, BBS_ID, ORD, AC1, AC2, AC3, AC4, AC5, AC6, AC7, AC8, AC9, AC10,
 AC11, AC12, AC13, AC14, AC15, AC16, AC17, AC18, AC19, AC20
 )
VALUES
( #{nttId}, #{bbsId}, #{ord}, #{ac1}, #{ac2}, #{ac3}, 
  #{ac4}, #{ac5}, #{ac6}, #{ac7}, 
  #{ac8}, #{ac9}, #{ac10}, #{ac11}, #{ac12}, #{ac13},
  #{ac14}, #{ac15}, #{ac16}, #{ac17}, #{ac18}, #{ac19}, #{ac20}
 )

mapper.select:
SELECT a.NTT_ID, a.NTT_SJ, a.NTT_CN, a.CATE_NAME, a.FRST_REGISTER_ID, IFNULL(b.USER_NM, a.NTCR_NM) as FRST_REGISTER_NM, DATE_FORMAT(a.FRST_REGIST_PNTTM, '%Y-%m-%d') as FRST_REGIST_PNTTM, a.RDCNT, a.PARNTSCTT_NO, a.ANSWER_AT, a.ANSWER_LC, a.USE_AT, a.ATCH_FILE_ID, a.BBS_ID, a.NTCE_BGNDE, a.NTCE_ENDDE, a.SJ_BOLD_AT, a.NOTICE_AT, a.SECRET_AT, c.COMMENT_CO FROM COMTNBBS a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID LEFT OUTER JOIN (SELECT NTT_ID, BBS_ID, COUNT(1) AS COMMENT_CO FROM COMTNCOMMENT WHERE USE_AT = 'Y' GROUP BY NTT_ID, BBS_ID) c ON a.NTT_ID = c.NTT_ID AND a.BBS_ID = c.BBS_ID WHERE a.BBS_ID = #{bbsId} AND a.USE_AT = 'Y'
AND a.NTT_SJ LIKE CONCAT ('%', #{searchWrd},'%')
AND a.NTT_CN LIKE CONCAT ('%', #{searchWrd},'%')
AND b.USER_NM LIKE CONCAT ('%', #{searchWrd},'%')
AND a.CATE_NAME = #{searchCate}
ORDER BY a.SORT_ORDR DESC, NTT_NO ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:

UPDATE BBSADDEDCOLMNS SET 
	USE_YN = 'N'
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}

mapper.select:

SELECT IFNULL(MAX(RDCNT),0)+1 AS RDCNT FROM COMTNBBS
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}

mapper.update:

UPDATE COMTNBBS SET 
	RDCNT = #{inqireCo},
	LAST_UPDUSR_ID = #{lastUpdusrId},
	LAST_UPDT_PNTTM = SYSDATE()
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}
