mapper.select:
SELECT SEQ, AUTH_NAME, ORGNZT_ID, GROUP_ID, ACCEPT_LINK, BAN_LINK, AUTH_PRIORITY, USE_YN, INS_ID, DATE_FORMAT(INS_DT,'%Y-%m-%dT%H:%i:%s') AS INS_DT, UPT_ID, DATE_FORMAT(UPT_DT,'%Y-%m-%dT%H:%i:%s') AS UPT_DT FROM AUTHMANAGE WHERE USE_YN = 'Y'
AND PAGE = #{page}
AND (PAGE = '' OR PAGE IS NULL)
AND ( 1=0 OR ORGNZT_ID=#{orgnztId}
OR GROUP_ID=#{groupId}
) ORDER BY SEQ ASC


    SELECT COUNT(SEQ)
      FROM AUTHMANAGE 
     WHERE USE_YN = 'Y' 

     AND PAGE = #{page}


     AND (PAGE = '' OR PAGE IS NULL)




     AND (
     1=0
     OR
        ORGNZT_ID=#{orgnztId}

     OR
        GROUP_ID=#{groupId}

	)
