mapper.select:
SELECT uniqId, userTy, userId , userNm, emailAdres, areaNo, middleTelno, endTelno, moblphonNo, groupId, sttus, sbscrbDe, membershipType,membershipStartDt,sendMailYn FROM( SELECT ESNTL_ID uniqId, 'USR01' userTy, MBER_ID userId, MBER_NM userNm, MBER_EMAIL_ADRES emailAdres, AREA_NO areaNo, MIDDLE_TELNO middleTelno, END_TELNO endTelno, MBTLNUM moblphonNo, GROUP_ID groupId, MBER_STTUS sttus, SBSCRB_DE sbscrbDe, MEMBERSHIP_TYPE membershipType, MEMBERSHIP_START_DT membershipStartDt, send_mail_yn sendMailYn FROM COMTNGNRLMBER ) A WHERE 1=1
AND sttus LIKE #{sbscrbSttus}
AND userId LIKE #{searchKeyword}
AND userNm LIKE '%' #{searchKeyword} '%'
ORDER BY uniqId DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(1) totcnt FROM( SELECT ESNTL_ID uniqId, 'USR01' userTy, MBER_ID userId, MBER_NM userNm, MBER_EMAIL_ADRES emailAdres, AREA_NO areaNo, MIDDLE_TELNO middleTelno, END_TELNO endTelno, MBTLNUM moblphonNo, GROUP_ID groupId, MBER_STTUS sttus, SBSCRB_DE sbscrbDe FROM COMTNGNRLMBER WHERE 1=1 AND MEMBERSHIP_TYPE LIKE #{membershipType} AND MEMBERSHIP_START_DT BETWEEN DATE(#{sbscrbDeBegin}) AND DATE(#{sbscrbDeEnd})
AND SBSCRB_DE BETWEEN DATE(#{sbscrbDeBegin}) AND DATE(#{sbscrbDeEnd})
) A WHERE 1=1
AND sttus LIKE #{sbscrbSttus}
AND userId LIKE #{searchKeyword}
AND userNm LIKE '%' #{searchKeyword} '%'

mapper.update:

    UPDATE COMTNGNRLMBER 
    SET 
           PASSWORD   =  #{password}
    WHERE  ESNTL_ID  = #{uniqId}

mapper.select:
/* mberManageDAO.selectMberListForSndngMail */ SELECT MBER_ID mberId, MBER_NM mberNm, MBER_EMAIL_ADRES mberEmailAdres, MEMBERSHIP_TYPE membershipType, MEMBERSHIP_START_DT membershipStartDt, MEMBERSHIP_EXPIRE_DT membershipExpireDt, SEND_MAIL_YN sendMailYn FROM COMTNGNRLMBER WHERE SEND_MAIL_YN = 'Y'
AND MEMBERSHIP_TYPE != 'N'
AND MEMBERSHIP_TYPE = 'N'

mapper.update:
    	/* mberManageDAO.updateMberTypeAfterExpire */
    	
    	UPDATE COMTNGNRLMBER 
		SET MEMBERSHIP_TYPE = 'N'
		WHERE 1=1
			AND MBER_ID = #{mberId}
			AND MEMBERSHIP_EXPIRE_DT < CURDATE()

mapper.select:
/* mberManageDAO.selectMberNearExpireList */
SELECT MBER_ID, MBER_EMAIL_ADRES, MEMBERSHIP_EXPIRE_DT FROM COMTNGNRLMBER WHERE MEMBERSHIP_TYPE != 'N'
AND DATEDIFF(MEMBERSHIP_EXPIRE_DT, CURDATE()) <= #{daysBefore} AND DATEDIFF(MEMBERSHIP_EXPIRE_DT, CURDATE()) >= '0'
