mapper.select:
	select SEQ, MBER_ID, REG_DATE, APPROVE_YN, TEAM_NAME, HEAD_NAME, PHONE, GENRE, AREA, PERSONNEL, MBERS, PROFILE, EQUIPMENT, SNS_LINK, SNS_VIDEO, T_FILE 
	from BUSKINGGROUP
	WHERE 1=1

      	and AREA =#{area}


      	and GENRE =#{genre}


      	and APPROVE_YN =#{approveYN}



      		and TEAM_NAME LIKE CONCAT('%' , #{searchKeyword}, '%')




      		and HEAD_NAME LIKE CONCAT('%' , #{searchKeyword}, '%')


     	and DELETE_YN=0
   ORDER BY SEQ DESC

    	LIMIT #{pageSize} OFFSET #{pageOffset}

	select count(SEQ) 
	from BUSKINGGROUP
	WHERE 1=1

	      	and AREA =#{area}


	      	and GENRE =#{genre}


	      	and APPROVE_YN =#{approveYN}



	      		and TEAM_NAME LIKE CONCAT('%' , #{searchKeyword}, '%')




	      		and HEAD_NAME LIKE CONCAT('%' , #{searchKeyword}, '%')


     	and DELETE_YN=0

mapper.update:
update 

	BUSKINGGROUP


	BUSKINGSTAGE

set APPROVE_YN=#{APPROVE_YN}

	, PLACE=#{PLACE}


	, TIME=#{TIME}


	, DATE_BGN=#{DATE_BGN}


where SEQ=#{SEQ}

update 

	BUSKINGGROUP


	BUSKINGSTAGE

set DELETE_YN=#{DELETE_YN} 
where SEQ=#{SEQ}

mapper.select:
	select count(SEQ) from BUSKINGSTAGE
	WHERE 1=1

			and PLACE = #{place}


			and APPROVE_YN = #{approveYN}




				and #{searchDateBgn} <= DATE_BGN 






					and #{searchDateEnd} >= DATE_BGN





				and (
					DATE_BGN between #{searchDateBgn} and #{searchDateEnd}
				)



			and TIME = #{searchTime}



	      		and TEAM_NM LIKE CONCAT('%' , #{searchKeyword}, '%')




	      		and HEAD_NM LIKE CONCAT('%' , #{searchKeyword}, '%')



     	and DELETE_YN=0

select S.SEQ , S.GROUP_SEQ , S.MBER_ID , DATE_FORMAT(S.REG_DATE,'%Y-%m-%d %H:%i:%s') AS REG_DATE , S.PROG_NM , S.PLACE , S.DATE_BGN , S.TIME , S.INTRO , S.EQUIPMENT , S.FILE , S.APPROVE_YN , S.DELETE_YN , S.TEAM_NM , S.HEAD_NM , S.PHONE from BUSKINGSTAGE S WHERE 1=1
and S.PLACE = #{place}
and S.APPROVE_YN = #{approveYN}
and #{searchDateBgn} <= DATE_BGN
and #{searchDateEnd} >= DATE_BGN
and ( DATE_BGN between #{searchDateBgn} and #{searchDateEnd} )
and TIME = #{searchTime}
and TEAM_NM LIKE CONCAT('%' , #{searchKeyword}, '%')
and HEAD_NM LIKE CONCAT('%' , #{searchKeyword}, '%')
and S.DELETE_YN=0 order by S.SEQ DESC
limit ${pageSize} offset #{pageOffset}

		update 
			BUSKINGSTAGE
		set APPROVE_YN=#{approveYN} 
		where SEQ in 

                    #{item}

mapper.insert:
insert into BUSKINGSTAGE(
	GROUP_SEQ
	, MBER_ID
	, TEAM_NM
	, HEAD_NM
	, PHONE
	, PROG_NM
	, PLACE
	, DATE_BGN
	, DATE_END
	, TIME
	, INTRO
	, EQUIPMENT
	, `FILE`
) values (
	#{GROUP_SEQ}
	, #{MBER_ID}
	, #{TEAM_NM}
	, #{HEAD_NM}
	, #{PHONE}
	, #{PROG_NM}
	, #{PLACE}
	, #{DATE_BGN}
	, #{DATE_END}
	, #{TIME}
	, #{INTRO}
	, #{EQUIPMENT}
	, #{FILE}
)
