mapper.select:
/* IndiplusDAO.selectRestDay */
SELECT SEQ, DATE, DATE_FORMAT(CREATE_DT,'%Y/%m/%d %H:%i:%s') AS CREATE_DT, DATE_FORMAT(UPDATE_DT,'%Y/%m/%d %H:%i:%s') AS UPDATE_DT FROM INDIPLUSRESTSCHEDULE ORDER BY SEQ DESC
LIMIT ${pageSize} OFFSET ${pageOffset}

mapper.update:
/* IndiplusDAO.updateRestDay */

UPDATE 
	INDIPLUSRESTSCHEDULE
SET 
	DATE = #{date}
WHERE 
	SEQ = #{seq}
