mapper.select:
/* PerformanceDAO.selectPerformanceApplierList */ SELECT DISTINCT AA.SEQ, AA.BBS_ID, AA.NTT_ID, AA.MBER_ID, AA.APL_DATE, AA.APL_NAME, AA.APL_PHONE, AA.APL_EMAIL, AA.APL_POSTCODE, AA.APL_ADDRESS, AA.MEMO, AA.FILE_ID, AA.RESULT, DATE_FORMAT(AA.CREATE_DT,'%Y-%m-%d %H:%i:%s') AS CREATE_DT, DATE_FORMAT(AA.UPDATE_DT,'%Y-%m-%d %H:%i:%s') AS UPDATE_DT FROM APPLIERINFO AA, COMTNBBS BB, BBSADDEDCOLMNS CC WHERE AA.BBS_ID = BB.BBS_ID AND AA.NTT_ID = BB.NTT_ID AND BB.BBS_ID = CC.BBS_ID AND BB.NTT_ID = CC.NTT_ID
AND AA.SEQ = #{SEQ}
AND BB.CATE_NAME LIKE "%" #{category} "%"
AND BB.NTT_SJ LIKE "%" #{subject} "%"
AND AA.APL_NAME LIKE "%" #{APL_NAME} "%"
AND AA.APL_PHONE LIKE "%" #{APL_PHONE} "%"
AND AA.APL_EMAIL LIKE "%" #{APL_EMAIL} "%"
AND AA.RESULT = #{RESULT}
ORDER BY AA.SEQ DESC
LIMIT ${pageSize} OFFSET ${pageOffset}

/* PerformanceDAO.selectPerformanceApplierListCnt */ WITH APPLIED_LIST AS ( SELECT DISTINCT AA.SEQ AS SEQ FROM APPLIERINFO AA, COMTNBBS BB, BBSADDEDCOLMNS CC WHERE AA.BBS_ID = BB.BBS_ID AND AA.NTT_ID = BB.NTT_ID AND BB.BBS_ID = CC.BBS_ID AND BB.NTT_ID = CC.NTT_ID
AND AA.SEQ = #{SEQ}
AND BB.CATE_NAME LIKE "%" #{category} "%"
AND BB.NTT_SJ LIKE "%" #{subject} "%"
AND AA.APL_NAME LIKE "%" #{APL_NAME} "%"
AND AA.APL_PHONE LIKE "%" #{APL_PHONE} "%"
AND AA.APL_EMAIL LIKE "%" #{APL_EMAIL} "%"
AND AA.RESULT = #{RESULT}
) SELECT COUNT(*) AS CNT FROM APPLIED_LIST

mapper.update:
/* PerformanceDAO.updatePerformanceAppliedData */
UPDATE
	APPLIERINFO
SET
	RESULT = #{RESULT}
WHERE 
	SEQ = #{SEQ}
