mapper.select:
/* PhcfMenuManageDAO.selectMenuInfoList */ SELECT DEPTH1_NUMBER, DEPTH2_NUMBER, DEPTH3_NUMBER, PAGE, MENU_NAME, LINK FROM MENUINFOLIST WHERE 1=1
AND PAGE = #{page}
AND PAGE = 'main'

mapper.update:
/* PhcfMenuManageDAO.updateRegMenu */
UPDATE 
	MENUINFOLIST
SET
	DEPTH1_NUMBER = #{DEPTH1_NUMBER}
	DEPTH1_NUMBER = #{DEPTH2_NUMBER}
	DEPTH1_NUMBER = #{DEPTH3_NUMBER}
	PAGE = #{PAGE}
	MENU_NAME = #{MENU_NAME}
	LINK = #{LINK}
	NEW_PAGE #{NEW_PAGE}
WHERE
	SEQ = #{SEQ}
