mapper.select:
/* premiumMember.selectMembershipRegList */ SELECT SEQ, MEM_ID, B.MBER_NM, BIRTH_DATE, ADDRESS1, ADDRESS2, PRE_TYPE, PAY_PRICE, SEND_SMS, SEND_MAIL, RESULT, CHECK_YN, DATE_FORMAT(CREATE_DT,'%Y/%m/%d %H:%i:%s') AS CREATE_DT, DATE_FORMAT(UPDATE_DT,'%Y/%m/%d %H:%i:%s') AS UPDATE_DT FROM MEMBERSHIPREGLOG A LEFT OUTER JOIN COMTNGNRLMBER B ON A.MEM_ID = B.MBER_ID WHERE 1=1
AND SEQ = #{SEQ}
AND ${searchType} LIKE '%' #{search_data} '%'
AND PRE_TYPE = #{type}
AND CHECK_YN = #{check_yn}
ORDER BY CHECK_YN ASC, CREATE_DT DESC
LIMIT ${pageSize} OFFSET ${pageOffset}

/* premiumMember.selectMembershipRegListCnt */
SELECT
	COUNT(*) AS LENGTH
FROM
	MEMBERSHIPREGLOG A
LEFT OUTER JOIN COMTNGNRLMBER B
ON A.MEM_ID = B.MBER_ID
WHERE 1=1

	AND SEQ = #{SEQ}


	AND ${searchType} LIKE '%' #{search_data} '%'


	AND PRE_TYPE = #{type}


	AND CHECK_YN = #{check_yn}

mapper.update:
/* premiumMember.updateMembershipStatus */
UPDATE MEMBERSHIPREGLOG SET RESULT = #{RESULT},
CHECK_YN = 'N'
CHECK_YN = 'Y'
WHERE SEQ = #{SEQ}

/* premiumMember.updateMembershipGrade */
UPDATE COMTNGNRLMBER SET
MEMBERSHIP_TYPE = #{TYPE}, MEMBERSHIP_START_DT = NOW(), MEMBERSHIP_EXPIRE_DT = MEMBERSHIP_START_DT + INTERVAL #{membershipDurationYear} YEAR
MEMBERSHIP_TYPE = 'N'
WHERE MBER_ID = #{ID}
