mapper.select:
/* premiumMember.selectMembershipRegList */ SELECT SEQ, MEM_ID, MEM_NAME, PRE_TYPE, PAY_PRICE, RESULT, CHECK_YN, DATE_FORMAT(CREATE_DT,'%Y/%m/%d %H:%i:%s') AS CREATE_DT, DATE_FORMAT(UPDATE_DT,'%Y/%m/%d %H:%i:%s') AS UPDATE_DT FROM MEMBERSHIPREGLOG WHERE 1=1
AND SEQ = #{SEQ}
AND ${searchType} LIKE '%' #{search_data} '%'
AND PRE_TYPE = #{type}
AND CHECK_YN = #{check_yn}
ORDER BY CHECK_YN ASC, CREATE_DT DESC
LIMIT ${pageSize} OFFSET ${pageOffset}

/* premiumMember.selectMembershipRegListCnt */
SELECT
	COUNT(*) AS LENGTH
FROM
	MEMBERSHIPREGLOG
WHERE 1=1

	AND SEQ = #{SEQ}


	AND ${searchType} LIKE '%' #{search_data} '%'


	AND PRE_TYPE = #{type}


	AND CHECK_YN = #{check_yn}
