	scg_id
	, scg_tp
	, scg_nm
	, scg_number
	, img_file_name
	, img_file_path
	, img_thum_v_name
	, img_thum_l_name
	, scg_url
) VALUES (
	#{scg_id}
	, #{user_tp}
	, #{user_comp_nm}
	, #{user_comp_number}
	, #{img_file_name}
	, #{img_file_path}
	, #{img_thum_v_name}
	, #{img_thum_l_name}
	, #{scg_url}
)
on duplicate key update
	img_file_name = #{img_file_name}
	, img_file_path = #{img_file_path}
	, img_thum_v_name = #{img_thum_v_name}
	, img_thum_l_name = #{img_thum_l_name}
	, scg_url = #{scg_url}

mapper.select:
/* 관리자화면 tb_support_cg_info 테이블을 조회하여 정보를 넘겨준다. */ SELECT scg_id /* 등록이미지 ID */ , scg_tp /* 회원종류 */ , scg_nm /* 회원이름(사업자명or단체명) */ , scg_number /* 회원번호(기업은 사업자번호, 단체는 없음) */ , img_file_name /* 파일저장이름 */ , img_file_path /* 파일저장경로 */ , img_thum_v_name /* 썸네일이미지 이름 */ , img_thum_l_name /* 썸네일이미지저장 위치 */ , scg_url /* 대표사이트 주소 */ FROM tb_support_cg_info WHERE 1=1
AND scg_tp = #{userTp}
AND scg_nm = #{compNm}
AND scg_number = #{compNumber}
ORDER BY create_dt desc LIMIT 1

