mapper.update:
/* 회원등급 정보를 업데이트 한다. */
UPDATE COMTNGNRLMBER 
SET 
	user_grade = #{user_grade} 
WHERE 
	mber_id = #{mber_id}

mapper.select:
/* CMS관리자용 후원정보 조회 */ SELECT sp_id /* seq */ , CASE WHEN user_tp = 'U' THEN '개인' WHEN user_tp = 'C' THEN '기업' ELSE '단체' END AS user_tp /* 후원유저종류 */ , IF(T2.user_grade IS NULL, '미분류', (SELECT code_nm FROM COMTCCMMNDETAILCODE WHERE code = T2.user_grade) ) AS user_grade /* 회원등급 */ , T1.user_id /* 회원ID (로그인 사용자 OR 비로그인 사용자 */ , CASE WHEN user_tp = 'U' THEN T1.user_nm ELSE T1.user_comp_nm END AS user_nm /* 휴원유저종류에 따라 유저명 또는 기업,단체명 입력 */ , CONCAT( user_phone1, '-', user_phone2, '-', user_phone3 ) AS user_phone /* 후원자 연락처 */ , IF(user_email1 IS NULL OR user_email2 IS NULL OR LENGTH(user_email1) = 0 OR LENGTH(user_email2) = 0, NULL, CONCAT( user_email1, '@', user_email2 ) ) AS user_email /* 후원자 이메일 */ , CASE WHEN sp_mh_tp = 'O' THEN '일시후원' ELSE '정기후원' END AS sp_mh_tp /* 후원방식 */ , CASE WHEN sc_price_tp = 'C' THEN '카드' WHEN sc_price_tp = 'E' THEN '계좌이체' WHEN sc_price_tp = 'M' THEN '무통장' WHEN sc_price_tp = 'D' THEN '카드' WHEN sc_price_tp = 'S' THEN 'CMS계좌' ELSE '없음' END AS sc_price_tp , DATE_FORMAT(T1.sc_price, '999,999,999,999,999') as sc_price /* 후원금액 */ , DATE_FORMAT(T1.create_dt, '%Y-%m-%d %H:%i:%S') as create_dt /* 등록일 */ , T1.browser /* 결제모드 */ , T1.ins_id /* 등록자 */ , T1.admin_msg /* 관리자감사메시지 */ FROM tb_support_log T1 /* 후원 log 테이블 */ LEFT JOIN ( SELECT mber_id_id, user_grade FROM COMTNGNRLMBER ) T2 /* 회원정보 테이블 */ ON T1.user_id = T2.mber_id WHERE 1=1
AND user_tp = #{search_user_tp}
ORDER BY sp_id desc LIMIT ${page_no}, ${page_size}

SELECT cms_id, user_id, CASE WHEN acc_tp = 'D' THEN '카드' WHEN acc_tp = 'S' THEN 'CMS' ELSE '기타' END AS acc_tp, agree_send_yn, user_send_yn, pay_send_yn, del_target_yn, del_send_yn, user_name, sc_price, sc_req_dd, sc_cnt, use_yn, create_dt, ins_id FROM tb_support_cms WHERE 1=1
AND user_id LIKE CONCAT('%',#{search_s},'%')
order by cms_id desc LIMIT ${page_no}, ${page_size}
