mapper.select:
/* VenueReservationDAO.selectVenueReservationRegList */ SELECT DISTINCT AA.SEQ, AA.VENUE, AA.USE_ROOM, AA.MANAGER_NAME, AA.EVENT_NAME, AA.ORGAN_NAME, AA.TELNUMBER, AA.TELNUMBER2, AA.EMAIL, AA.FILE_ID, AA.USER_ID, AA.RESULT, AA.CHECK_YN, DATE_FORMAT(AA.CREATE_DT,'%Y-%m-%d %H:%i:%s') AS CREATE_DT, DATE_FORMAT(AA.UPDATE_DT,'%Y-%m-%d %H:%i:%s') AS UPDATE_DT FROM VENUERESERVATIONREG AA, VENUERESERVATIONDATES BB WHERE 1=1 AND AA.SEQ = BB.REG_ID
AND AA.RESULT NOT IN ('C','D','O')
AND AA.VENUE = #{venueCnd}
AND AA.RESULT = #{resultCnd}
AND AA.${searchCnd} LIKE '%' #{keyword} '%'
AND BB.USE_DATE LIKE '%' #{currentDate} '%'
ORDER BY AA.SEQ DESC
LIMIT ${pageSize} OFFSET ${pageOffset}

/* VenueReservationDAO.selectVenueReservationRegListCnt */ SELECT COUNT(*) FROM VENUERESERVATIONREG WHERE 1=1
AND RESULT NOT IN ('C','D','O')
AND VENUE = #{venueCnd}
AND RESULT = #{resultCnd}
AND (USE_DATE1 = #{keyword} OR USE_DATE2 = #{keyword} OR USE_DATE3 = #{keyword} OR USE_DATE4 = #{keyword} OR USE_DATE5 = #{keyword})
AND ${searchCnd} LIKE '%' #{keyword} '%'
