/* VenueReservationDAO.selectVenueReservationRegList */ SELECT SEQ, VENUE, USE_ROOM, USE_DATE1, USE_DATE2, USE_DATE3, USE_DATE4, USE_DATE5, DATE_FORMAT(USE_START_TIME1,'%H:%i') AS USE_START_TIME1, DATE_FORMAT(USE_END_TIME1,'%H:%i') AS USE_END_TIME1,
DATE_FORMAT(USE_START_TIME2,'%H:%i') AS USE_START_TIME2, DATE_FORMAT(USE_END_TIME2,'%H:%i') AS USE_END_TIME2,
DATE_FORMAT(USE_START_TIME3,'%H:%i') AS USE_START_TIME3, DATE_FORMAT(USE_END_TIME3,'%H:%i') AS USE_END_TIME3,
DATE_FORMAT(USE_START_TIME4,'%H:%i') AS USE_START_TIME4, DATE_FORMAT(USE_END_TIME4,'%H:%i') AS USE_END_TIME4,
DATE_FORMAT(USE_START_TIME5,'%H:%i') AS USE_START_TIME5, DATE_FORMAT(USE_END_TIME5,'%H:%i') AS USE_END_TIME5, MANAGER_NAME, MANAGER_GRADE, EVENT_NAME, ORGAN_NAME, TELNUMBER, EMAIL, FILE_ID, USER_ID, RESULT, CHECK_YN, CREATE_DT, UPDATE_DT FROM VENUERESERVATIONREG WHERE 1=1
AND VENUE = #{venueCnd}
AND RESULT = #{resultCnd}
AND ${searchCnd} LIKE '%' #{keyword} '%'
