mapper.update:
UPDATE COMTNGNRLMBER
SET
	user_grade = #{user_grade}
WHERE
	mber_id = #{user_id}

UPDATE tb_support_log
SET
	admin_msg = #{admin_msg}
WHERE
	sp_id = #{sp_id}

mapper.select:
/* CMS관리자용 후원정보 전체 건수 */ SELECT count(1) as totCnt FROM tb_support_log WHERE 1=1
AND user_tp = #{user_tp}
AND T2.user_grade = #{user_grade}
AND T1.user_id = #{user_id}
AND user_nm like CONCAT('%', #{user_nm}, '%')
AND user_comp_nm like CONCAT('%', #{user_nm}, '%')
AND ( user_phone1 like CONCAT('%', #{user_phone}, '%') or user_phone2 like CONCAT('%', #{user_phone}, '%') or user_phone3 like CONCAT('%', #{user_phone}, '%') )
AND ( user_email1 like CONCAT('%', #{user_email}, '%') or user_email2 like CONCAT('%', #{user_email}, '%') )
AND sp_mh_tp = #{sp_mh_tp}
AND sc_price_tp = #{sc_price_tp}

/* CMS관리자용 후원정보 조회 */ SELECT sp_id /* seq */ , user_tp /* 후원유저종류 */ , T2.user_grade /* 회원등급 */ , T1.user_id /* 회원ID (로그인 사용자 OR 비로그인 사용자 */ , ( CASE user_tp WHEN 'U' THEN T1.user_nm ELSE T1.user_comp_nm END ) AS user_nm /* 휴원유저종류에 따라 유저명 또는 기업,단체명 입력 */ , CONCAT( user_phone1, '-', user_phone2, '-', user_phone3 ) AS user_phone /* 후원자 연락처 */ , IF(user_email1 IS NULL OR user_email2 IS NULL OR LENGTH(user_email1) = 0 OR LENGTH(user_email2) = 0, NULL, CONCAT( user_email1, '@', user_email2 ) ) AS user_email /* 후원자 이메일 */ , sp_mh_tp /* 후원방식 */ , sc_price_tp /* 결제방식 */ , T1.sc_price /* 후원금액 */ , DATE_FORMAT(T1.create_dt, '%Y-%m-%d %H:%i:%S') as create_dt /* 등록일 */ , T1.browser /* 결제모드 */ , T1.ins_id /* 등록자 */ , T1.admin_msg /* 관리자감사메시지 */ FROM tb_support_log T1 /* 후원 log 테이블 */ LEFT JOIN ( SELECT mber_id, user_grade FROM COMTNGNRLMBER ) T2 /* 회원정보 테이블 */ ON T1.user_id = T2.mber_id WHERE 1=1
AND user_tp = #{user_tp}
AND T2.user_grade = #{user_grade}
AND T1.user_id = #{user_id}
AND user_nm like CONCAT('%', #{user_nm}, '%')
AND user_comp_nm like CONCAT('%', #{user_nm}, '%')
AND ( user_phone1 like CONCAT('%', #{user_phone}, '%') or user_phone2 like CONCAT('%', #{user_phone}, '%') or user_phone3 like CONCAT('%', #{user_phone}, '%') )
AND ( user_email1 like CONCAT('%', #{user_email}, '%') or user_email2 like CONCAT('%', #{user_email}, '%') )
AND sp_mh_tp = #{sp_mh_tp}
AND sc_price_tp = #{sc_price_tp}
ORDER BY sp_id desc LIMIT ${pageSize} OFFSET ${firstIndex}
